mapper.select:
select a.act_no accountingNo, g.g_no gno, g.name gname, act.act_cate_no actno, act.name actname, a.title, a.amount, a.reg_dt, a.status from gms_accounting a inner join gms_accounting_cate act on (a.act_cate_no = act.act_cate_no) left outer join gms_group g on (a.g_no = g.g_no)
where a.g_no = #{groupNo} order by a.act_no desc limit #{offset}, #{rowCount}
where a.g_no = #{groupNo} and act.act_cate_no = #{actCate} order by a.act_no desc limit #{offset}, #{rowCount}

select count(*) from gms_accounting a inner join gms_accounting_cate act on (a.act_cate_no = act.act_cate_no) left outer join gms_group g on (a.g_no = g.g_no)
where g.g_no = #{groupNo}
where g.g_no = #{groupNo} and act.act_cate_no = #{actCate}
