mapper.update:
update gms_faq set
title = #{title},
content = #{content}
where
faq_no = #{no}

mapper.select:
SELECT faq.faq_no, faq.main_cate_no, faq.query_no, faq.title, faq.content, faq.path, faq.reg_dt, gqc.name gqcn FROM gms_faq faq, gms_query_cate gqc WHERE faq.query_no = gqc.query_no
AND faq.title LIKE "%"#{searchKeyword}"%"
order by faq.faq_no DESC LIMIT #{no}, #{cutno}
