mapper.select:
select pm.pm_b_no, pm.title, pm.content, pm.reg_dt, pm.view_cnt, m.memb_no mno, m.membername mname, p.pups_no pno, p.name pname, a.act_local_no ano, a.name_si asi, a.name_gu agu from gms_pickme pm inner join gms_memb m on pm.memb_no=m.memb_no inner join gms_activelocal a on pm.act_local_no=a.act_local_no inner join gms_purpose p on p.pups_no=pm.pups_no
order by pm.pm_b_no desc limit #{offset}, #{rowCount}
where a.name_si like #{nameSi} order by pm.pm_b_no desc limit #{offset}, #{rowCount}
where a.act_local_no = #{nameGu} order by pm.pm_b_no desc limit #{offset}, #{rowCount}

select count(*) from gms_pickme pm inner join gms_memb m on pm.memb_no=m.memb_no inner join gms_activelocal a on pm.act_local_no=a.act_local_no inner join gms_purpose p on p.pups_no=pm.pups_no
where a.name_si like #{nameSi}
where a.act_local_no = #{nameGu}

mapper.delete:
delete from gms_pickme
where 
  pm_b_no = #{no} and memb_no = #{member.no}
